
function search_table(min_char) {
  // Declare variables
 var input, filter, table, tr, td, i,j;
  input = document.getElementById("searchtable");
  filter = input.value.toUpperCase();
  table = document.getElementById("csvtable");
  tr = table.getElementsByTagName("tr");
  if(typeof min_char !='number')
  min_char = 1;
  odd = true
  // Loop through all table rows, and hide those who don't match the search query
  if(filter.length>=min_char)
  {
    for (i = 1; i < tr.length; i++) {
      td = tr[i].getElementsByTagName("td") ;
      tr[i].classList.remove('rowshow', 'even', 'odd');
      for(j=0 ; j<td.length ; j++)
      {
      
        let tdata = td[j] ;
        if (tdata) {
          if (tdata.innerHTML.toUpperCase().indexOf(filter) > -1) {
            if (odd) {
              tr[i].classList.add('odd');
              odd = false
            }
            else 
            {
              tr[i].classList.add('even');
              odd = true
            }
            tr[i].classList.add('rowshow');
            tr[i].style.display = "";
            break ;
          } else {
          tr[i].classList.remove('rowshow');
            tr[i].style.display = "none";
          }
        }
      
      }
    }
  }
  else
  {
    for (i = 1; i < tr.length; i++) {
      td = tr[i].getElementsByTagName("td") ;
      tr[i].classList.remove('rowshow', 'even', 'odd');
      for(j=0 ; j<td.length ; j++)
      {
      
        let tdata = td[j] ;
        if (tdata) {
          if (tdata.innerHTML.toUpperCase().indexOf(filter) > -1) {
            if (odd) {
              tr[i].classList.add('odd');
              odd = false
            }
            else 
            {
              tr[i].classList.add('even');
              odd = true
            }
            tr[i].classList.add('rowshow');
            tr[i].style.display = "";
            break ;
          }
        }
      
      }
    }

  }
}